import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Part.css';

function dispatchClick(setPlayed, dispatch, position, currentPlay) {
  setPlayed(true);
  dispatch({ type: 'JOGAR', play: { position, currentPlay } });
}

function findCurrentPlay(plays) {
  if (plays.length === 0) {
    return 1;
  }
  if (plays[plays.length - 1].currentPlay === 1) {
    return 2;
  }
  return 1;
}

function findClassName(plays, position) {
  const shouldBeenClicked =
    plays.length > 0 ? plays.find((play) => play.position === position) : false;
    console.log(shouldBeenClicked);
}

function Part({ position }) {
  const plays = useSelector((state) => state);
  const [played, setPlayed] = useState(false);
  //   const [currentPlay, setCurrentPlay] = useState(1);
  const dispatch = useDispatch();

  useEffect(() => {
    if (plays.length === 0 || plays[plays.length - 1].position === position) {
      setCurrentPlay(findCurrentPlay(plays));
    }
  });
  findClassName(plays, position);
  return (
    <div
      onClick={() => dispatchClick(setPlayed, dispatch, position, currentPlay)}
      className={plays.length === 0 ? 'notPlayed' : `play${currentPlay}`}
    ></div>
  );
}

export default Part;
