import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Part.css';

function dispatchClick(setPlayed, dispatch, position, currentPlay) {
  setPlayed(true);
  dispatch({ type: 'JOGAR', play: { position, currentPlay } });
}

function findCurrentPlay(plays) {
  if (plays.length === 0) {
    return 1;
  }
  return plays[plays.length - 1].currentPlay === 1 ? 2 : 1;
}

function Part({ position }) {
  const [played, setPlayed] = useState(false);
  const [currentPlay, setCurrentPlay] = useState(findCurrentPlay(plays));
  const plays = useSelector((state) => state);
  const dispatch = useDispatch();
  return (
    <div
      onClick={() => dispatchClick(setPlayed, dispatch, position, currentPlay)}
      className={played ? `play${currentPlay}` : 'notPlayed'}
    ></div>
  );
}

export default Part;
