import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Part.css';

function dispatchClick(setPlayed, dispatch, position) {
  setPlayed(true);
  dispatch({ type: 'JOGAR', play: { position, currentPlay } });
}

// function findCurrentPlay(plays) {
