function Part({ position }) {
  const [played, setPlayed] = useState(false);
  const plays = useSelector((state) => state);
  const dispatch = useDispatch();
  console.log(plays);
  return (
    <div
      onClick={() => dispatchClick(setPlayed, dispatch, position)}
      className={played ? `play${1}` : 'notPlayed'}
    ></div>
  );
}

export default Part;
