function Game() {
  const plays = useSelector((state) => state.data);
  console.log(plays);
  if (plays.filter((play) => play === 1).length > 2) {
    const selecteds = plays.map((play) => play === 1);
    console.log(selecteds);
  }
  return <div>{<Container />}</div>;
}

export default Game;
