import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Part.css';

function dispatchClick(setPlayed, dispatch, position, player, changePlayer) {
  setPlayed(true);
  dispatch({ type: 'JOGAR', play: { position, player } });
  return changePlayer();
}

function Part({ position, player, changePlayer }) {
  const plays = useSelector((state) => state);
  const dispatch = useDispatch();
  const [played, setPlayed] = useState(false);

  return (
    <div
      onClick={() =>
        dispatchClick(setPlayed, dispatch, position, player, changePlayer)
      }
      className={played ? `play${player}` : 'notPlayed'}
    ></div>
  );
}

export default Part;
