import React, { useState } from 'react';
import { connect } from 'react-redux';
import './Part.css';

function dispatchClick(setPlayed) {
  setPlayed(true);
//   setCurrentPlay(currentPlay === 1 ? 2 : 1);
}

function findCurrentPlay() {

}

function Part({ position, plays }) {
  const [played, setPlayed] = useState(false);
  console.log(plays);
  return (
    <div
      onClick={() => dispatchClick(setPlayed)}
      className={played ? `play${findCurrentPlay()}` : 'notPlayed'}
    ></div>
  );
}

const mapStateToProps = (state) => ({
    plays: state.reducer,
});

export default connect(mapStateToProps)(Part);
